    /**
     * Get a reference font size.
     * @param $size (string) String containing font size value.
     * @param $refsize (float) Reference font size in points.
     * @return float value in points
     * @public static
     */
    public static function getFontRefSize($size, $refsize=12) {
        switch ($size) {
            case 'xx-small': {
                $size = ($refsize - 4);
                break;
            }
            case 'x-small': {
                $size = ($refsize - 3);
                break;
            }
            case 'small': {
                $size = ($refsize - 2);
                break;
            }
            case 'medium': {
                $size = $refsize;
                break;
            }
            case 'large': {
                $size = ($refsize + 2);
                break;
            }
            case 'x-large': {
                $size = ($refsize + 4);
                break;
            }
            case 'xx-large': {
                $size = ($refsize + 6);
                break;
            }
            case 'smaller': {
                $size = ($refsize - 3);
                break;
            }
            case 'larger': {
                $size = ($refsize + 3);
                break;
            }
        }
        return $size;
    }








































