    /**
     * Adds a new page to the document. If a page is already present, the Footer() method is called first to output the footer (if enabled). Then the page is added, the current position set to the top-left corner according to the left and top margins (or top-right if in RTL mode), and Header() is called to display the header (if enabled).
     * The origin of the coordinate system is at the top-left corner (or top-right for RTL) and increasing ordinates go downwards.
     * @param $orientation (string) page orientation. Possible values are (case insensitive):<ul><li>P or PORTRAIT (default)</li><li>L or LANDSCAPE</li></ul>
     * @param $format (mixed) The format used for pages. It can be either: one of the string values specified at getPageSizeFromFormat() or an array of parameters specified at setPageFormat().
     * @param $keepmargins (boolean) if true overwrites the default page margins with the current margins
     * @param $tocpage (boolean) if true set the tocpage state to true (the added page will be used to display Table Of Content).
     * @public
     * @since 1.0
     * @see startPage(), endPage(), addTOCPage(), endTOCPage(), getPageSizeFromFormat(), setPageFormat()
     */
    public function AddPage($orientation='', $format='', $keepmargins=false, $tocpage=false) {
        if ($this->inxobj) {
            // we are inside an XObject template
            return;
        }
        if (!isset($this->original_lMargin) OR $keepmargins) {
            $this->original_lMargin = $this->lMargin;
        }
        if (!isset($this->original_rMargin) OR $keepmargins) {
            $this->original_rMargin = $this->rMargin;
        }
        // terminate previous page
        $this->endPage();
        
        // get phpdocx values
        if (isset($this->infoPhpdocx['section'])) {
            if (empty($orientation) && isset($this->infoPhpdocx['section']['orientation'])) {
                $orientation = $this->infoPhpdocx['section']['orientation'];
            }
            if (empty($format) && isset($this->infoPhpdocx['section']['size'])) {
                $format = $this->infoPhpdocx['section']['size'];
            }
        }

        // start new page
        $this->startPage($orientation, $format, $tocpage);

        // get phpdocx values
        if (isset($this->infoPhpdocx['section'])) {
            if (isset($this->infoPhpdocx['section']['color'])) {
                $colorRGB = str_split($this->infoPhpdocx['section']['color'], 2);

                $this->SetFillColor(hexdec($colorRGB[0]), hexdec($colorRGB[1]), hexdec($colorRGB[2]));
            }
            if (isset($this->infoPhpdocx['section']['width']) && isset($this->infoPhpdocx['section']['height'])) {
                $this->Rect(-1, -1, $this->infoPhpdocx['section']['width'] + 2, $this->infoPhpdocx['section']['height'] + 2, 'DF', '');
            }

            if (isset($this->infoPhpdocx['section']['borderTop'])) {
                $this->SetLineStyle($this->infoPhpdocx['section']['borderTop']['lineStyle']);
                $this->Line($this->infoPhpdocx['section']['borderTop']['line'][0], $this->infoPhpdocx['section']['borderTop']['line'][1], $this->infoPhpdocx['section']['borderTop']['line'][2], $this->infoPhpdocx['section']['borderTop']['line'][3]);
            }

            if (isset($this->infoPhpdocx['section']['borderRight'])) {
                $this->SetLineStyle($this->infoPhpdocx['section']['borderRight']['lineStyle']);
                $this->Line($this->infoPhpdocx['section']['borderRight']['line'][0], $this->infoPhpdocx['section']['borderRight']['line'][1], $this->infoPhpdocx['section']['borderRight']['line'][2], $this->infoPhpdocx['section']['borderRight']['line'][3]);
            }

            if (isset($this->infoPhpdocx['section']['borderBottom'])){
                $this->SetLineStyle($this->infoPhpdocx['section']['borderBottom']['lineStyle']);
                $this->Line($this->infoPhpdocx['section']['borderBottom']['line'][0], $this->infoPhpdocx['section']['borderBottom']['line'][1], $this->infoPhpdocx['section']['borderBottom']['line'][2], $this->infoPhpdocx['section']['borderBottom']['line'][3]);
            }

            if (isset($this->infoPhpdocx['section']['borderLeft'])) {
                $this->SetLineStyle($this->infoPhpdocx['section']['borderLeft']['lineStyle']);
                $this->Line($this->infoPhpdocx['section']['borderLeft']['line'][0], $this->infoPhpdocx['section']['borderLeft']['line'][1], $this->infoPhpdocx['section']['borderLeft']['line'][2], $this->infoPhpdocx['section']['borderLeft']['line'][3]);
            }

        }
    }

    /**
     * Terminate the current page
     * @param $tocpage (boolean) if true set the tocpage state to false (end the page used to display Table Of Content).
     * @public
     * @since 4.2.010 (2008-11-14)
     * @see AddPage(), startPage(), addTOCPage(), endTOCPage()
     */
    public function endPage($tocpage=false) {
        // check if page is already closed
        if (($this->page == 0) OR ($this->numpages > $this->page) OR (!$this->pageopen[$this->page])) {
            return;
        }
        // print page footer
        $this->setFooter();
        // close page
        $this->_endpage();
        // mark page as closed
        $this->pageopen[$this->page] = false;
        if ($tocpage) {
            $this->tocpage = false;
        }
    }

    /**
     * Starts a new page to the document. The page must be closed using the endPage() function.
     * The origin of the coordinate system is at the top-left corner and increasing ordinates go downwards.
     * @param $orientation (string) page orientation. Possible values are (case insensitive):<ul><li>P or PORTRAIT (default)</li><li>L or LANDSCAPE</li></ul>
     * @param $format (mixed) The format used for pages. It can be either: one of the string values specified at getPageSizeFromFormat() or an array of parameters specified at setPageFormat().
     * @param $tocpage (boolean) if true the page is designated to contain the Table-Of-Content.
     * @since 4.2.010 (2008-11-14)
     * @see AddPage(), endPage(), addTOCPage(), endTOCPage(), getPageSizeFromFormat(), setPageFormat()
     * @public
     */
    public function startPage($orientation='', $format='', $tocpage=false) {
        if ($tocpage) {
            $this->tocpage = true;
        }
        // move page numbers of documents to be attached
        if ($this->tocpage) {
            // move reference to unexistent pages (used for page attachments)
            // adjust outlines
            $tmpoutlines = $this->outlines;
            foreach ($tmpoutlines as $key => $outline) {
                if (!$outline['f'] AND ($outline['p'] > $this->numpages)) {
                    $this->outlines[$key]['p'] = ($outline['p'] + 1);
                }
            }
            // adjust dests
            $tmpdests = $this->dests;
            foreach ($tmpdests as $key => $dest) {
                if (!$dest['f'] AND ($dest['p'] > $this->numpages)) {
                    $this->dests[$key]['p'] = ($dest['p'] + 1);
                }
            }
            // adjust links
            $tmplinks = $this->links;
            foreach ($tmplinks as $key => $link) {
                if (!$link['f'] AND ($link['p'] > $this->numpages)) {
                    $this->links[$key]['p'] = ($link['p'] + 1);
                }
            }
        }
        if ($this->numpages > $this->page) {
            // this page has been already added
            $this->setPage($this->page + 1);
            $this->SetY($this->tMargin);
            return;
        }
        // start a new page
        if ($this->state == 0) {
            $this->Open();
        }
        ++$this->numpages;
        $this->swapMargins($this->booklet);
        // save current graphic settings
        $gvars = $this->getGraphicVars();
        // start new page
        $this->_beginpage($orientation, $format);
        // mark page as open
        $this->pageopen[$this->page] = true;
        // restore graphic settings
        $this->setGraphicVars($gvars);
        // mark this point
        $this->setPageMark();
        // print page header
        $this->setHeader();
        // restore graphic settings
        $this->setGraphicVars($gvars);
        // mark this point
        $this->setPageMark();
        // print table header (if any)
        $this->setTableHeader();
        // set mark for empty page check
        $this->emptypagemrk[$this->page]= $this->pagelen[$this->page];
    }

